"use strict";const socket=io("http://localhost:1338");console.log(io);const messageContainer=document.getElementById("message-container"),messageForm=document.getElementById("send-container"),messageInput=document.getElementById("message-input"),appendMessage=e=>{const n=document.createElement("div");n.innerText=e,messageContainer.append(n)};if(null!=messageForm){messageForm.addEventListener("submit",e=>{e.preventDefault();const n=messageInput.value;appendMessage(`You: ${n}`),socket.emit("client-chat-message",n),messageInput.value=""});const e=prompt("what is your name?");appendMessage("You joined"),socket.emit("new-user-mainfloor",e)}socket.on("connect",(function(e){socket.emit("join","Server Connected to Client")})),socket.on("messages",(function(e){console.log(e)})),socket.on("server-chat-message",(function(e){appendMessage(`${e.name}: ${e.message}`)})),socket.on("user-connected-mainfloor",(function(e){appendMessage(`${e} connected`)})),socket.on("user-disconnected-mainfloor",(function(e){appendMessage(`${e} disconnected`)}));